import React, { Component } from 'react';
import axios from 'axios';
const API = "https://63a5721a318b23efa793a770.mockapi.io/api/produce/"
class Edit extends Component {
  constructor(props) {
    super(props);
    this.state = {
      product: {
        id: '',
        name: '',
        avatar: '',
        quantity: '',
        description: '',
      }
    };
  }
  

  handleInputChange = (event) => {
    const { name, value } = event.target;
    this.setState(prevState => ({
      product: {
        ...prevState.product,
        [name]: value
      }
    }));
  };

  handleSubmit = async (event) => {
    event.preventDefault();

    const { product } = this.state;
    if (window.confirm("Bạn có chắc chắn muốn lưu thay đổi?")) {
      // Thực hiện các thao tác cập nhật sách
      const updatedProduct = {
        id: this.state.id,
        name: this.state.name,
        avatar: this.state.avatar,
        quatity: this.state.quatity,
        description: this.state.description
      };
  
      // Thực hiện gửi yêu cầu cập nhật sách
      axios
        .put(API + this.state.id, updatedProduct)
        .then(response => {
          console.log(response);
          // Cập nhật state và hiển thị thông báo
          const updatedProductList = this.state.product.map(item => {
            if (item.id === this.state.id) {
              return { ...item, ...updatedProduct };
            }
            return item;
          });
          this.setState({
            product: updatedProductList,
            showEditForm: false
          });
          alert("Sửa thành công!");
          window.location = '/Admin'; // Replace with your desired URL
        })
        .catch(error => {
          console.log(error);
        });
    }
  }

  render() {
    const { product } = this.state;

    return (
      <div className="container">
        <div className="row">
          <div className="col-sm-12">
            <div className="card">
              <div className="card-body">
                <form onSubmit={this.handleSubmit}>
                  <div className="form-group">
                    <label>ID</label>
                    <input type="text" className="form-control" value={product.id} onChange={this.handleInputChange} name="id" />
                  </div>
                  <div className="form-group">
                  <label>Tên Sản phẩm</label>
                  <input type="text" className="form-control" value={product.name} onChange={this.handleInputChange} name="name" />
                </div>
                <div className="form-group">
                  <label>Ảnh sản phẩm</label>
                  <input type="text" className="form-control" value={this.state.product.avatar} onChange={this.handleInputChange} name="avatar" />
                </div>
                <div className="form-group">
                  <label>Gía</label>
                  <input type="text" className="form-control" value={product.quantity} onChange={this.handleInputChange} name="quantity" />{/* Sửa thành 'quantity' */}
                </div>
                <div className="form-group">
                  <label>Mô tả</label>
                  <input type="text" className="form-control" value={product.description} onChange={this.handleInputChange} name="description" />{/* Sửa thành 'quantity' */}
                </div>
                  <button type="submit" className="btn btn-primary">Save</button>
                </form>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default Edit;
