import React, { Component } from 'react';
import { Link } from 'react-router-dom';

class PageAdmin extends Component {
  constructor(props) {
    super(props);
    this.state = {
      products: []
    };
  }

  componentDidMount() {
    fetch("https://63a5721a318b23efa793a770.mockapi.io/api/produce")
      .then(response => response.json())
      .then(products => this.setState({ products }));
  }

  render() {
    const { products } = this.state;

    return (
      <div>
        <table className="table">
          <thead className="thead-dark">
          <tr>
                <th>ID</th>
                <th>Tên sản phẩm</th>
                <th>Ảnh sản phẩm</th>
                <th>Gía</th>
                <th>Mô tả sản phẩm</th>
                                                <th>Xóa</th>
                                                <th>Sửa</th>

                                            </tr>
          </thead>
          <tbody>
            {products.map((product, index) => (
              <tr key={index}>
                <td>{product.name}</td>
                <td><img src={product.image} style={{ width: 100 }} alt={product.name} /></td>
                <td>{product.price}</td>
                <td>{product.color}</td>
                <td>{product.name_category}</td>
                <td>{product.material}</td>
                <td>{product.expiry_date}</td>
                <td>{product.origin}</td>
                <td>{product.description}</td>
                <td style={{ color: product.tinhtranghang ? 'green' : 'gray' }}>{product.tinhtranghang ? 'Available' : 'Sold out'}</td>
                <td style={{ display: 'flex' }}>
                  <button className="btn btn-primary mr-2">
                    <Link to={`/Edit/${product.id}`} style={{ color: 'white' }}>Edit</Link>
                  </button>
                  <button className="btn btn-danger">
                    <Link to={`/Delete/${product.id}`} style={{ color: 'white' }}>Delete</Link>
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    );
  }
}

export default PageAdmin;
