import React, { useState, useEffect } from 'react';
import { Link, useHistory } from 'react-router-dom';
import axios from 'axios';
import CartPage from './CartPage';
import '../style/Show.css';
import 'bootstrap/dist/css/bootstrap.min.css';

const Show = () => {
  const [data, setData] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [searchResults, setSearchResults] = useState([]);
  const [cartItems, setCartItems] = useState([]);
  const [showCartPage, setShowCartPage] = useState(false);
  const history = useHistory();

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    try {
      const response = await axios.get('https://6410c403da042ca131fb737e.mockapi.io/haisan');
      setData(response.data);
      setSearchResults(response.data);
    } catch (error) {
      console.log(error);
    }
  };

  const handleSearch = (e) => {
    setSearchTerm(e.target.value);
  };

  const handleSearchButton = () => {
    const results = data.filter((item) =>
      item.name.toLowerCase().includes(searchTerm.toLowerCase())
    );
    setSearchResults(results);
  };

  const addToCart = async (item) => {
    try {
      // Thêm thông tin người dùng vào item
      const loggedInUser = localStorage.getItem('user');
      const user = JSON.parse(loggedInUser);
      
      const cartItem = {
        ...item,
        user: user
      };

      await axios.post('https://6410c403da042ca131fb737e.mockapi.io/gioHang', cartItem);
      setCartItems([...cartItems, cartItem]);
      console.log('Thêm vào giỏ hàng thành công');
      alert('Thêm vào giỏ hàng thành công!');
    } catch (error) {
      console.log('Lỗi khi thêm vào giỏ hàng:', error);
    }
  };

  const handleCartIconClick = () => {
    setShowCartPage(true);
    history.push('/cart');
  };

  if (showCartPage) {
    return <CartPage cartItems={cartItems} />;
  }

  return (
    <div className="container">
      <header>
        <h1>Website của bạn</h1>
        <button onClick={handleCartIconClick}>Giỏ hàng</button>
      </header>
      <br />
      {/* Các phần khác của giao diện */}
    </div>
  );
};

export default Show;
