import axios from "axios";
import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import ProductDetail from "./ProductDetail";
import { Link } from 'react-router-dom';

export const Shopping = () => {
  const { id } = useParams();
  const [cartItems, setCartItems] = useState([]);

  const addToCart = async (item) => {
    try {
      await axios.post(`https://63a5721a318b23efa793a770.mockapi.io/api/Type_Product`, item);
      setCartItems([...cartItems, item]);
      console.log("Thêm vào giỏ hàng thành công");
    } catch (error) {
      console.log("Lỗi khi thêm vào giỏ hàng:", error);
    }
  };

  const removeFromCart = async (itemId) => {
    const updatedCart = cartItems.filter((item) => item.id !== itemId);
    setCartItems(updatedCart);
    try {
      await axios.delete(`https://63a5721a318b23efa793a770.mockapi.io/api/Type_Product/${itemId}`);
      console.log("Xóa hàng thành công");
    } catch (error) {
      console.log("Lỗi khi xóa:", error);
    }
  };

  useEffect(() => {
    const storedCartItems = JSON.parse(localStorage.getItem("cartItems")) || [];
    setCartItems(storedCartItems);
    getproduct();
  }, []);

  useEffect(() => {
    localStorage.setItem("cartItems", JSON.stringify(cartItems));
  }, [cartItems]);

  const getproduct = async () => {
    const value = await axios.get(
      `https://63a5721a318b23efa793a770.mockapi.io/api/produce/${id}`
    );
    const product = value.data;
    setCartItems([product]);
  };

  return (
    <div className="container">
    <div className="cart">
      <h3>Giỏ hàng</h3>
      {cartItems.length > 0 ? (
        <table className="table table-bordered">
          <thead>
            <tr>
              <th>STT</th>
              <th>Tên sản phẩm</th>
              <th>Hình ảnh</th>
              <th>Giá</th>
              <th>Thao tác</th>
            </tr>
          </thead>
          <tbody>
            {cartItems.map((item, index) => (
              <tr key={item.id}>
                <td>{index + 1}</td>
                <td>{item.name}</td>
                <td>
                  <img src={item.avatar} alt={item.name} />
                </td>
                <td>{item.quatity}</td>
                <td style={{ display: 'flex', gap: '10px' }}>
                  <button className="btn btn-warning" onClick={() => removeFromCart(item.id)}>Xóa</button>
                  <button className="btn btn-warning">Mua ngay</button>
                  <Link className='btn btn-warning' to={`/product/${item.id}`}>Chi tiết</Link>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      ) : (
        <p>Giỏ hàng trống</p>
      )}
    </div>
    </div>
  );
};

export default Shopping;
