import axios from "axios";
import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import ProductDetail from "./ProductDetail";

export const Shopping = () => {
  const { id } = useParams();
  const [cartItems, setCartItems] = useState([]);
  const [successMessage, setSuccessMessage] = useState("");

  const removeFromCart = async (itemId) => {
    const updatedCart = cartItems.filter((item) => item.id !== itemId);
    setCartItems(updatedCart);

    try {
      await axios.delete(
        `https://63a5721a318b23efa793a770.mockapi.io/api/Type_Product/${itemId}`
      );
      console.log("Đã xóa thành công");
    } catch (error) {
      console.log("Không xóa được:", error);
    }
  };

  const addToCart = (item) => {
    const updatedCart = [...cartItems, item];
    setCartItems(updatedCart);
    setSuccessMessage("Đã thêm vào giỏ hàng thành công");
  };

  useEffect(() => {
    getproduct();
  }, []);

  const getproduct = async () => {
    const value = await axios.get(
      `https://63a5721a318b23efa793a770.mockapi.io/api/produce/${id}`
    );
    const product = value.data;
    setCartItems([product]);
  };

  return (
    <div className="cart">
      <h3>Giỏ hàng</h3>
      {successMessage && <p>{successMessage}</p>}
      {cartItems.length > 0 ? (
        <table className="table table-bordered">
          <thead>
            <tr>
              <th>STT</th>
              <th>Tên sản phẩm</th>
              <th>Hình ảnh</th>
              <th>Giá</th>
              <th>Thao tác</th>
            </tr>
          </thead>
          <tbody>
            {cartItems.map((item, index) => (
              <tr key={item.id}>
                <td>{index + 1}</td>
                <td>{item.name}</td>
                <td>
                  <img src={item.avatar} alt={item.name} />
                </td>
                <td>{item.quatity}</td>
                <td style={{ display: "flex", gap: "10px" }}>
                  <button
                    className="btn btn-warning"
                    onClick={() => removeFromCart(item.id)}
                  >
                    Xóa
                  </button>
                  <button
                    className="btn btn-warning"
                    onClick={() => addToCart(item)}
                  >
                    Mua ngay
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      ) : (
        <p>Giỏ hàng trống</p>
      )}
    </div>
  );
};

export default Shopping;
