import React from 'react';

const CartPage = ({ cartItems, removeFromCart }) => {
  return (
    <div className="container">
      <h3>Giỏ hàng</h3>
      {cartItems.length === 0 ? (
        <p>Giỏ hàng trống</p>
      ) : (
        <ul>
          {cartItems.map((item) => (
            <li key={item.id}>
              {item.name} - {item.price}
              <button onClick={() => removeFromCart(item.id)}>Xóa</button>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default CartPage;
