import React from 'react';

const Card = ({ item, addToCart }) => {
  return (
    <div className="col-md-3" key={item.id}>
      <div className="card">
        <img src={item.avatar} alt={item.name} className="card-img-top" />
        <div className="card-body">
          <h4 className="card-title">{item.name}</h4>
          <p className="card-text">{item.description}</p>
          <p className="card_price"> {item.quatity}</p>
          <div className="function">
            <button className="button1" onClick={() => addToCart(item)}>
              THÊM GIỎ HÀNG
            </button>
         
            </div>
        </div>
      </div>
    </div>
  );
};

export default Card;
