import React, { useState, useEffect } from 'react';
const fetchData = async () => {
    try {
      const response = await fetch('https://63a5721a318b23efa793a770.mockapi.io/api/produce');
      const json = await response.json();
      setData(json);
      setSearchResults(json);
    } catch (error) {
      console.log(error);
    }
  };
  useEffect(() => {
    fetchData();
  }, []);
const ProductDetail = ({ match, searchResults }) => {
  const productId = match.params.id;
  const product = searchResults.find((item) => item.id === productId);

  // Kiểm tra nếu sản phẩm không tồn tại
  if (!product) {
    return <div>Không tìm thấy sản phẩm</div>;
  }

  return (
    <div className="container">
      <h3 className="colection">Chi tiết sản phẩm</h3>
      <div className="card">
      <img src={product.avatar} alt={product.name} className="card-img-top" />
        <div className="card-body">
          <h4 className="card-title">{product.name}</h4>
          <p className="card-text">{product.description}</p>
          <p className="card_price">{product.quatity}</p>
        </div>
      </div>
    </div>
  );
};

export default ProductDetail;
