import axios from 'axios'
import React, { useEffect, useState } from 'react'
import {Container,Card,Table,Input} from "reactstrap"
export const Payment = () => {
    const [payment,setPayment]=useState([])
    const [sum,setSum]=useState()
    useEffect(()=>{
        getToPay()
       

    },[])

    useEffect(()=>{
        let sum=0;
        payment.forEach((item)=>(
            sum+=parseFloat(item.price)
        ))
        setSum(sum)
    },[payment])
    const getToPay=async ()=>{
        try {
            const cart=await axios.get(`https://63ea318ee0ac9368d64c75fa.mockapi.io/Product_1`)
            setPayment(cart.data)
        } catch (error) {
            console.log(error)
        }
    }
    const handelDelete=async (id)=>{
        await axios.delete(`https://63ea318ee0ac9368d64c75fa.mockapi.io/Product_1/${id}`)
        const product=payment.filter((item)=>{
            return item.id!=id
        })
        setPayment(product)

    }
  return (
    <div>
        <Container>
            <Card>
                <div className="cart-hearder">
                    <h1 className='text-center'>Shopping Cart</h1>
                </div>
                <div className="cart-body">
                    <Table className='table table-bordered'>
                        <thead>
                            <tr>
                                <th>STT</th>
                                <th>Title</th>
                                <th>Image</th>
                                <th>Price</th>
                                <th>Action</th>
                            </tr>
                            
                        </thead>
                        <tbody>
                        { payment.map((item,index)=>(
                            <tr>
                                <td>{index+1}</td>
                                <td>{item.title}</td>
                                <td><img src={item.image} alt="" /></td>
                                <td>{item.price}</td>
                                <td><button type='submit' onClick={()=>handelDelete(item.id)} className='btn btn-warning'>Delete</button></td>
                            </tr>
                        ))}
                            
                        </tbody>
                    </Table>
                </div>
                <div className="cart-footer">  
                        <Input value={sum}></Input>
                    
                    <button type='submit' className='btn btn-success'>Mua ngay</button>
                </div>
            </Card>
        </Container>
    </div>
  )
}
