import React, { useState, useEffect } from 'react';
import '../style/Show.css';
import 'bootstrap/dist/css/bootstrap.min.css';

const Show = () => {
  const [data, setData] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [searchResults, setSearchResults] = useState([]);
  const [cartItems, setCartItems] = useState([]);

  const fetchData = async () => {
    try {
      const response = await fetch('https://63a5721a318b23efa793a770.mockapi.io/api/produce');
      const json = await response.json();
      setData(json);
      setSearchResults(json);
    } catch (error) {
      console.log(error);
    }
  };

  useEffect(() => {
    fetchData();
  }, []);

  const handleSearch = (e) => {
    setSearchTerm(e.target.value);
  };

  const handleSearchButton = () => {
    const results = data.filter((item) =>
      item.name.toLowerCase().includes(searchTerm.toLowerCase())
    );
    setSearchResults(results);
  };

  const addToCart = (item) => {
    setCartItems([...cartItems, item]);
  };

  return (
    <div className="container">
      <br></br>
      <h3 className="colection">SẢN PHẨM NỔI BẬT</h3>
      <br></br>
      <div className="row">
        <input
          type="text"
          placeholder="Search..."
          value={searchTerm}
          onChange={handleSearch}
        />
        <button onClick={handleSearchButton}>Tìm kiếm</button>
        {searchResults.map((e) => (
          <div className="col-md-3" key={e.id}>
            <div className="card">
              <img src={e.avatar} alt={e.name} className="card-img-top" />
              <div className="card-body">
                <h4 className="card-title">{e.name}</h4>
                <p className="card-text">{e.description}</p>
                <p className='card_price'>{e.quatity}</p>
                <div className='function'>
                  <button className="button1" onClick={() => addToCart(e)}>
                    THÊM GIỎ HÀNG
                  </button>
                </div>
              </div>
            </div>
          </div>
        ))}
      </div>
      <div className="cart">
        <h3>Giỏ hàng</h3>
        {cartItems.length > 0 ? (
          <ul>
            {cartItems.map((item) => (
              <li key={item.id}>{item.name}</li>
            ))}
          </ul>
        ) : (
          <p>Giỏ hàng trống</p>
        )}
      </div>
    </div>
  );
};

export default Show;
