import axios from "axios";
import { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";

export const Shopping = () => {
  const { id } = useParams();
  const [cartItems, setCartItems] = useState([]);
  const navigate = useNavigate();
  // ...
  
  const addToCart = async () => {
    try {
      // ...
      console.log("Thêm vào giỏ hàng thành công");
    } catch (error) {
      console.log("Lỗi khi thêm vào giỏ hàng:", error);
    }
  };
  
  const handleBack = () => {
    navigate(-1); // Quay lại trang trước đó
  };
  
  const handleClearCart = () => {
    // ...
    console.log("Đã xóa giỏ hàng");
  };
  
  // ...
  
 return (
  <div className="container">
    <div className="product-details">
      <h1>Product Details</h1>
      {/* Render the product details */}
      <h2>Name: {product.name}</h2>
      <img src={product.avatar} alt={product.name} />
      <p>Quantity: {product.quantity}</p>

      {/* Add to Cart button */}
      <button className="btn btn-primary" onClick={addToCart}>
        Add to Cart
      </button>

      {/* Back button */}
      <button className="btn btn-secondary" onClick={handleBack}>
        Back
      </button>
    </div>

    <div className="cart">
      <h3>Giỏ hàng</h3>

      {/* Render the cart items if there are any */}
      {cartItems.length > 0 ? (
        <table className="table table-bordered">
          {/* Table header */}
          <thead>
            <tr>
              <th>STT</th>
              <th>Tên</th>
              <th>Tên sản phẩm</th>
              <th>Hình ảnh</th>
              <th>Giá</th>
              <th>Thao tác</th>
            </tr>
          </thead>

          {/* Table body */}
          <tbody>
            {cartItems.map((item, index) => (
              <tr key={item.id}>
                <td>{index + 1}</td>
                <td>{item.name}</td>
                <td>
                  <img src={item.avatar} alt={item.name} />
                </td>
                <td>{item.quantity}</td>
                <td style={{ display: "flex", gap: "10px" }}>
                  {/* Remove from Cart button */}
                  <button
                    className="btn btn-warning"
                    onClick={() => removeFromCart(item.id)}
                  >
                    Xóa
                  </button>

                  {/* Buy Now button */}
                  <button className="btn btn-warning">Mua ngay</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      ) : (
        // Render a message when the cart is empty
        <p>Giỏ hàng trống</p>
      )}

      {/* Render a Clear Cart button if there are items in the cart */}
      {cartItems.length > 0 && (
        <button className="btn btn-danger" onClick={handleClearCart}>
          Xóa giỏ hàng
        </button>
      )}
    </div>
  </div>
);

};

export default Shopping;
