import axios from 'axios';
import React, { Component } from 'react';
import { Container, Card, Table, Input } from "reactstrap";

class Payment extends Component {
    constructor(props) {
        super(props);
        this.state = {
            payment: [],
            sum: null
        };
    }

    componentDidMount() {
        this.getToPay();
    }

    componentDidUpdate(prevProps, prevState) {
        if (prevState.payment !== this.state.payment) {
            let sum = 0;
            this.state.payment.forEach((item) => {
                sum += parseFloat(item.price);
            });
            this.setState({ sum });
        }
    }

    getToPay = async () => {
        try {
            const cart = await axios.get(`https://63a5721a318b23efa793a770.mockapi.io/api/produce`);
            this.setState({ payment: cart.data });
        } catch (error) {
            console.log(error);
        }
    }

    handelDelete = async (id) => {
        await axios.delete(`https://63a5721a318b23efa793a770.mockapi.io/api/produce/${id}`);
        const product = this.state.payment.filter((item) => item.id !== id);
        this.setState({ payment: product });
    }

    render() {
        const { payment, sum } = this.state;
        return (
            <div>
                <Container>
                    <Card>
                        <div className="cart-hearder">
                            <h1 className='text-center'>Shopping Cart</h1>
                        </div>
                        <div className="cart-body">
                            <Table className='table table-bordered'>
                                <thead>
                                    <tr>
                                        <th>STT</th>
                                        <th>Tên sản phẩm</th>
                                        <th>Ảnh sản phẩm</th>
                                        <th>Gía</th>
                                        <th>Mô tả sản phẩm</th>
                                        <th>Action</th>
                                    </tr>
                                </thead>
                                <tbody>
                                    {payment.map((item, index) => (
                                        <tr key={item.id}>
                                            <td>{index + 1}</td>
                                            <td>{item.name}</td>
                                            <td><img src={item.image} alt={item.name} /></td>
                                            <td>{item.quality}</td>
                                            <td><button type='submit' onClick={() => this.handelDelete(item.id)} className='btn btn-warning'>Delete</button></td>
                                        </tr>
                                    ))}
                                </tbody>
                            </Table>
                        </div>
                        <div className="cart-footer">
                            <Input value={sum} />
                            <button type='submit' className='btn btn-success'>Mua ngay</button>
                        </div>
                    </Card>
                </Container>
            </div>
        );
    }
}

export default Payment;
