import React, { useState } from "react";
import axios from "axios";
import '../style/signup.css'
const Signup = () => {
  const [username, setUsername] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [errors, setErrors] = useState({});

  // Validation
  const validate = () => {
    const errors = {};

    // Kiểm tra trường username
    if (username.trim() === "") {
      errors.username = "Vui lòng nhập tên đăng nhập";
    }

    // Kiểm tra trường email
    if (email.trim() === "") {
      errors.email = "Vui lòng nhập email";
    } else if (!email.includes("@")) {
      errors.email = "Email không hợp lệ";
    }

    // Kiểm tra trường password
    if (password === "") {
      errors.password = "Vui lòng nhập mật khẩu";
    } else if (password.length < 8) {
      errors.password = "Mật khẩu phải có ít nhất 8 kí tự";
    }

    return errors;
  };

  const handleChange = (event) => {
    const { name, value } = event.target;
    const updatedErrors = { ...errors };
    delete updatedErrors[name]; // Xóa lỗi của trường khi người dùng nhập liệu
    setErrors(updatedErrors);

    // Cập nhật giá trị của trường dữ liệu
    if (name === "username") {
      setUsername(value);
    } else if (name === "email") {
      setEmail(value);
    } else if (name === "password") {
      setPassword(value);
    }
  };


  // const handleUsernameChange = (event) => {
  //   setUsername(event.target.value);
  // };

  // const handleEmailChange = (event) => {
  //   setEmail(event.target.value);
  // };

  // const handlePasswordChange = (event) => {
  //   setPassword(event.target.value);
  // };

  const handleSubmit = (event) => {
    event.preventDefault();

    const validationErrors = validate();

    // Kiểm tra xem có lỗi hay không
    if (Object.keys(validationErrors).length === 0) {
      // Gửi yêu cầu POST đến MockAPI
      axios
        .post("https://63a572122a73744b008e28d5.mockapi.io/api/account", signupData)
        .then((response) => {
          console.log("Đăng ký thành công:", response.data);
          // Thực hiện các xử lý sau khi đăng ký thành công
        })
        .catch((error) => {
          console.error("Đăng ký thất bại:", error);
          // Thực hiện các xử lý khi đăng ký thất bại
        });
    } else {
      setErrors(validationErrors);
    }
  };


  return (
    <div className="signup-container">
      <h2>Singup</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Username:</label>
          <input type="text" name="username" value={username} onChange={handleChange} />
          {errors.username && <span className="error">{errors.username}</span>}
        </div>
        <div>
          <label>Email:</label>
          <input type="email" name="email" value={email} onChange={handleChange} />
          {errors.email && <span className="error">{errors.email}</span>};
        </div>
        <div>
          <label>Password:</label>
          <input type="password" name="password" value={password} onChange={handleChange} />
          {errors.password && <span className="error">{errors.email}</span>};
        </div>
        <button type="submit">Singup</button>
      </form>
    </div>
  );
};

export default Signup;
