import React from 'react';
import '../style/Show.css';
import 'bootstrap/dist/css/bootstrap.min.css';


class Show extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      data: [],
    };
  }

  async componentDidMount() {
    const response = await fetch('https://63a5721a318b23efa793a770.mockapi.io/api/produce');
    const json = await response.json();
    this.setState({ data: json });
  }

  render() {
    return (
      <div className='show'>
        <div className='product'>
      <div className="container">
        <br></br>
      <h3 className="colection">SẢN PHẨM NỔI BẬT</h3>
      <br></br>
      <div className="row">
        {this.state.data.map((e) => (
          <div className="col-md-3" key={e.id}>
            <div className="card">
              <img src={e.avatar} alt={e.name} className="card-img-top" />
              <div className="card-body">
                <h4 className="card-title">{e.name}</h4>
                <p className="card-text">{e.description}</p>
                <p className='card_price'>{e.quatity}</p>
                <button className="btn btn-primary">Mua ngay</button>
                <a href="Create.js">
                  <button className="btn btn-primary">Thêm giỏ hàng</button>
                </a>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
    </div>
   </div> 
    );
  }
}

export default Show;
