import React from 'react';
import Show from './Show_product';

const Cart = ({ cartItems, removeFromCart, updateQuantity }) => {
  const handleIncreaseQuantity = (itemId) => {
    updateQuantity(itemId, 2); // Increase quantity by 2
  };

  return (
    <div className="cart">
      <h3>Giỏ hàng</h3>
      {cartItems.length > 0 ? (
        <table className="table table-bordered">
          <thead>
            <tr>
              <th>STT</th>
              <th>Tiêu đề</th>
              <th>Hình ảnh</th>
              <th>Số lượng</th>
              <th>Giá</th>
              <th>Thao tác</th>
            </tr>
          </thead>
          <tbody>
            {cartItems.map((item, index) => (
              <tr key={item.id}>
                <td>{index + 1}</td>
                <td>{item.name}</td>
                <td>
                  <img src={item.avatar} alt={item.name} />
                </td>
                <td>{item.quantity}</td>
                <td>{item.price}</td>
                <td>
                  <button className="btn btn-warning" onClick={() => removeFromCart(item.id)}>
                    Xóa
                  </button>
                  <button className="btn btn-primary" onClick={() => handleIncreaseQuantity(item.id)}>
                    Tăng số lượng
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      ) : (
        <p>Giỏ hàng trống</p>
      )}
    </div>
  );
};

export default Cart;
