import React, { Component } from 'react';
import axios from 'axios';

class Add extends Component {
  constructor(props) {
    super(props);
    this.state = {
      newProduct: {
        name: '',
        quatity: '',
        avatar: '',
        description: '',
        id: ''
      }
    };
  }

  componentDidMount() {
    this.fetchProductCount();
  }

  fetchProductCount = async () => {
    try {
      const response = await axios.get('https://63a5721a318b23efa793a770.mockapi.io/api/produce');
      const products = response.data;
      const count = products.length;
      this.setState(prevState => ({
        newProduct: {
          ...prevState.newProduct,
          id: String(count + 1)
        }
      }));
    } catch (error) {
      console.error('Error fetching product count:', error);
    }
  };

  handleInputChange = (event) => {
    const { name, value } = event.target;
    this.setState(prevState => ({
      newProduct: {
        ...prevState.newProduct,
        [name]: value
      }
    }));
  };

  handleSubmit = async (event) => {
    event.preventDefault();
    const { newProduct } = this.state;

    try {
      await axios.post('https://63a5721a318b23efa793a770.mockapi.io/api/produce', newProduct);
      this.setState({
        newProduct: {
          name: '',
          quatity: '',
          avatar: '',
          description: '',
          id: Number(newProduct.id) + 1
        }
      });

      alert('Product added successfully!');
      setTimeout(() => {
        window.location = 'http://localhost:3001';
      }, 100);
    } catch (error) {
      alert('Error adding product:', error);
    }
  };

  render() {
    const { newProduct } = this.state;

    return (
      <div className="container">
            <div className="row">
              <div className="col-sm-12">
                <div className="card">
                  <div className="card-body">
                    <div className="form-group">
                      <label>Tên Sản phẩm</label>
                      <input type="text" className="form-control" value={this.state.name} onChange={(e) => this.setState({ name: e.target.value })} />
                    </div>
                    <div className="form-group">
                      <label>Ảnh sản phẩm</label>
                      <input type="text" className="form-control" value={this.state.avatar} onChange={(e) => this.setState({ avatar: e.target.value })} />
                    </div>
                    <div className="form-group">
                      <label>Gía</label>
                      <input type="text" className="form-control" value={this.state.quatity} onChange={(e) => this.setState({ quatity: e.target.value })} /> {/* Sửa thành 'quantity' */}
                    </div>
                    <div className="form-group">
                      <label>Mô tả</label>
                      <input type="text" className="form-control" value={this.state.description} onChange={(e) => this.setState({ description: e.target.value })} /> {/* Sửa thành 'quantity' */}
                    </div>
                    <button type="button" className="btn btn-primary" onClick={this.addBook}>Add</button>
                  </div>
                </div>
              </div>
            </div>
          </div>
    );
  }
}

export default Add;
