import axios from "axios";
import { useEffect, useState } from "react";
import { useParams, useHistory } from "react-router-dom";
import { Link } from "react-router-dom";

export const Shopping = () => {
  const { id } = useParams();
  const history = useHistory();
  const [product, setProduct] = useState([]);
  const [cartItems, setCartItems] = useState([]);

  const addToCart = async () => {
    try {
      await axios.post(
        "https://63a5721a318b23efa793a770.mockapi.io/api/Type_Product",
        product
      );
      setCartItems([...cartItems, product]);
      console.log("Thêm vào giỏ hàng thành công");
    } catch (error) {
      console.log("Lỗi khi thêm vào giỏ hàng:", error);
    }
  };

  const removeFromCart = async (itemId) => {
    const updatedCart = cartItems.filter((item) => item.id !== itemId);
    setCartItems(updatedCart);
    try {
      await axios.delete(
        `https://63a5721a318b23efa793a770.mockapi.io/api/Type_Product/${itemId}`
      );
      console.log("Xóa hàng thành công");
    } catch (error) {
      console.log("Lỗi khi xóa:", error);
    }
  };

  useEffect(() => {
    const storedCartItems = JSON.parse(localStorage.getItem("cartItems")) || [];
    setCartItems(storedCartItems);
    getProduct();
  }, []);

  useEffect(() => {
    localStorage.setItem("cartItems", JSON.stringify(cartItems));
  }, [cartItems]);

  const getProduct = async () => {
    const response = await axios.get(
      `https://63a5721a318b23efa793a770.mockapi.io/api/produce/${id}`
    );
    setProduct(response.data);
  };

  const handleBack = () => {
    history.goBack();
  };

  const handleClearCart = () => {
    setCartItems([]);
    localStorage.removeItem("cartItems");
    console.log("Đã xóa giỏ hàng");
  };

  return (
    <div className="container">
      <div className="product-details">
        <h1>Product Details</h1>
        <h2>Name: {product.name}</h2>
        <img src={product.avatar} alt={product.name} />
        <p>Quantity: {product.quantity}</p>
        <button className="btn btn-primary" onClick={addToCart}>
          Add to Cart
        </button>
        <button className="btn btn-secondary" onClick={handleBack}>
          Back
        </button>
      </div>
      <div className="cart">
        <h3>Giỏ hàng</h3>
        {cartItems.length > 0 ? (
          <table className="table table-bordered">
            <thead>
              <tr>
                <th>STT</th>
                <th>Tên</th>
                <th>Tên sản phẩm</th>
                <th>Hình ảnh</th>
                <th>Giá</th>
                <th>Thao tác</th>
              </tr>
            </thead>
            <tbody>
              {cartItems.map((item, index) => (
                <tr key={item.id}>
                  <td>{index + 1}</td>
                  <td>{item.name}</td>
                  <td>
                    <img src={item.avatar} alt={item.name} />
                  </td>
                  <td>{item.quantity}</td>
                  <td style={{ display: "flex", gap: "10px" }}>
                    <button
                      className="btn btn-warning"
                      onClick={() => removeFromCart(item.id)}
                    >
                      Xóa
                    </button>
                    <button className="btn btn-warning">Mua ngay</button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        ) : (
          <p>Giỏ hàng trống</p>
        )}
        {cartItems.length > 0 && (
          <button className="btn btn-danger" onClick={handleClearCart}>
            Xóa giỏ hàng
          </button>
        )}
      </div>
    </div>
  );
};

export default Shopping;
