import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import '../style/admin.css';

const API = "https://63a5721a318b23efa793a770.mockapi.io/api/produce/";

const ShowAdmin = () => {
  const [products, setProducts] = useState([]);

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    try {
      const response = await axios.get(API);
      setProducts(response.data);
    } catch (error) {
      console.log(error);
    }
  };

  return (
    <div className="container">
      <div className="row">
        <div className="col-sm-12">
          <div className="card">
            <div className="card-body">
              <h4 className="Title_table">Quản lý sản phẩm</h4>
              <p className="card-text">
                <button className="btn btn-primary">
                  <Link to={`/Add`} style={{ color: 'white', textDecoration: 'none' }}>
                    Thêm sản phẩm mới
                  </Link>
                </button>
              </p>
              <table className="table table-bordered">
                <thead>
                  <tr>
                    <th>ID</th>
                    <th>Tên sản phẩm</th>
                    <th>Ảnh sản phẩm</th>
                    <th>Gía</th>
                    <th>Mô tả sản phẩm</th>
                    <th>Sửa</th>
                    <th>Xóa</th>
                  </tr>
                </thead>
                <tbody>
                  {products.map((product) => (
                    <tr key={product.id}>
                      <td>{product.id}</td>
                      <td>{product.name}</td>
                      <td>
                        <img className="img2" src={product.avatar} alt={product.name} />
                      </td>
                      <td>{product.quatity}</td>
                      <td>{product.description}</td>
                      <td>
                        <button className="btn btn-primary mr-2">
                          <Link to={`/Edit/${product.id}`} style={{ color: 'white' }}>
                            Edit
                          </Link>
                        </button>
                      </td>
                      <td>
                        <button className="btn btn-primary mr-2">
                          <Link to={`/Delete/${product.id}`} style={{ color: 'white' }}>
                            Delete
                          </Link>
                        </button>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ShowAdmin;
