import React, { Component } from 'react';
import axios from 'axios';
const API = "https://63a5721a318b23efa793a770.mockapi.io/api/produce/"
class Add extends Component {
  constructor(props) {
    super(props);
    this.state = {
      newProduct: {
        name: "",
        avatar: "",
        quatity: "",
        description:"",
        // id: ''
      }
    };
  }

  componentDidMount() {
    axios
        .get(API)
        .then(response => {
            this.setState({ product: response.data });
        })
        .catch(error => {
            console.log(error);
        });
}

  handleInputChange = (event) => {
    const { name, value } = event.target;
    this.setState(prevState => ({
      newProduct: {
        ...prevState.newProduct,
        [name]: value
      }
    }));
  };

  handleSubmit = async (event) => {
    event.preventDefault();
    const { newProduct } = this.state;
  
    try {
      await axios.post(API, newProduct);
      this.setState({
        newProduct: {
          name: "",
          avatar: "",
          quatity: "",
          description: "",
          // id: Number(newProduct.id) + 1
        }
      });
  
      alert('Thêm mới thành công!');
      setTimeout(() => {
        window.location = '/Admin'; // Replace with your desired URL
      }, 100);
    } catch (error) {
      alert('Thêm mới không thành công:', error);
    }
  };
  
  render() {
    const { newProduct } = this.state;

    return (
      <div className="container">
      <div className="row">
        <div className="col-sm-12">
          <div className="card">
            <div className="card-body">
              <div className="form-group">
                <label>Tên Sản phẩm</label>
                <input type="text" className="form-control" onChange={this.handleInputChange} value={this.state.name} onChange={(e) => this.setState({ name: e.target.value })} />
              </div>
              <div className="form-group">
                <label>Ảnh sản phẩm</label>
                <input type="text" className="form-control"onChange={this.handleInputChange} value={this.state.avatar} onChange={(e) => this.setState({ avatar: e.target.value })} />
              </div>
              <div className="form-group">
                <label>Gía</label>
                <input type="text" className="form-control" onChange={this.handleInputChange} value={this.state.quatity} onChange={(e) => this.setState({ quatity: e.target.value })} /> {/* Sửa thành 'quantity' */}
              </div>
              <div className="form-group">
                <label>Mô tả</label>
                <input type="text" className="form-control" value={this.state.description} onChange={(e) => this.setState({ description: e.target.value })} /> {/* Sửa thành 'quantity' */}
              </div>
              <button type="submit" className="btn btn-primary">Add</button>
            </div>
          </div>
        </div>
      </div>
    </div>
    );
  }
}

export default Add;
