import axios from "axios"
import { useEffect, useState } from "react"
import { useParams } from "react-router-dom"
import { Link} from 'react-router-dom';
export const Shopping=()=>{ 
    const {id}=useParams()
    const [product,setproduct]=useState([])

    const [storeProduct,setStoreProduct]=useState({
        name:product.name,
        avatar:product.avatar,
        quantity:product.quantity
    })

    useEffect(()=>{
        getproduct()
    },[])

    useEffect(()=>{
         addshopping()
    },[])
    const addshopping=async()=>{
        await axios.post("https://63a5721a318b23efa793a770.mockapi.io/api/Type_Product",{
            name:product.name,
            avatar:product.avatar,
            quantity:product.quantity

        })
    }

    const getproduct=async()=>{
        const value=await axios.get(`https://63a5721a318b23efa793a770.mockapi.io/api/produce/${id}`)
        await axios.post("https://63a5721a318b23efa793a770.mockapi.io/api/Type_Product",value.data)
    }
    return (           <table className="table table-bordered">
    <thead>

        <tr>
            <th>ID</th>
            <th>Tên sản phẩm</th>
            <th>Ảnh sản phẩm</th>
            <th>Gía</th>
            <th>Sửa</th>
            <th>Xóa</th>

        </tr>
    </thead>

    <tbody>
        {this.state.product.map((products) =>(
            <tr >
                <td><textbox type="text" name="id" onChange={this.handleChange} /> {products.id}</td>
                <td>{products.name}</td>
                <td><img className="img2" src={products.avatar} alt={products.name} /></td>
                <td>{products.quatity}</td>
                <td> 
                <button className="btn btn-primary mr-2">
                <Link className='button' to={`/product/${e.id}`}>Chi tiết</Link>
                </button>
                </td>
                <td>
                <button type="submit"  className="btn btn-primary mr-2" >
                        <a style={{color: 'white',textDecoration:'none'}} className="btn btn-primary mr-2" href={`/Delete/${products.id}`}>Delete</a>
                </button>
                </td>
            </tr>
        ))}
        </tbody>
</table>
    )
}
export default Shopping;
