import React, { Component } from 'react';
import axios from 'axios';

const API = "https://63a5721a318b23efa793a770.mockapi.io/api/produce/";

class Edit extends Component {
  constructor(props) {
    super(props);
    this.state = {
      product: {
        id:'',
        name: '',
        avatar: '',
        quantity: '',
        description: '',
      }
    };
  }

  componentDidMount() {
    const { match } = this.props;
    if (match && match.params) {
      const { id } = match.params;
      axios.get(`https://63a5721a318b23efa793a770.mockapi.io/api/produce/${id}`)
        .then(response => {
          const data = response.data;
          this.setState({ product: data });
        })
        .catch(error => {
          console.log('Error fetching product:', error);
        });
    }
  }
  
  handleInputChange = (event) => {
    const { name, value } = event.target;
    this.setState(prevState => ({
      product: {
        ...prevState.product,
        [name]: value
      }
    }));
  };

  handleSubmit = async (event) => {
    event.preventDefault();
    const { id } = this.props.match.params;
    const { product } = this.state;

    try {
      await axios.put(`${API}/${id}`, product);
      this.setState({
        product: {
          name: '',
          avatar: '',
          quantity: '',
          description: '',
        }
      });

      alert('Product edited successfully!');

      setTimeout(() => {
        this.props.history.push('/Admin'); // Redirect to Admin page after editing
      }, 100);
    } catch (error) {
      console.log('Error editing product:', error);
    }
  };

  render() {
    const { product } = this.state;
  
    return (
      <div className="container">
        <div className="row">
          <div className="col-sm-12">
            <div className="card">
              <div className="card-body">
              <div className="form-group">
                  <label>ID</label>
                  <input type="text" className="form-control" value={product.id} onChange={this.handleInputChange} name="id" />
                </div>
                <div className="form-group">
                  <label>Tên Sản phẩm</label>
                  <input type="text" className="form-control" value={product.name} onChange={this.handleInputChange} name="name" />
                </div>
                <div className="form-group">
                  <label>Ảnh sản phẩm</label>
                  <input type="text" className="form-control" value={product.avatar} onChange={this.handleInputChange} name="avatar" />
                </div>
                <div className="form-group">
                  <label>Số lượng</label>
                  <input type="text" className="form-control" value={product.quantity} onChange={this.handleInputChange} name="quantity" />
                </div>
                <div className="form-group">
                  <label>Mô tả</label>
                  <input type="text" className="form-control" value={product.description} onChange={this.handleInputChange} name="description" />
                </div>
                <button type="submit" className="btn btn-primary" onClick={this.handleSubmit}>Update</button>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }
  
}

export default Edit;
