import React, { useState } from "react";
import axios from "axios";
import "../style/Login.css";

const Login = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  const handleEmailChange = (event) => {
    setEmail(event.target.value);
  };

  const handlePasswordChange = (event) => {
    setPassword(event.target.value);
  };

  const handleSubmit = (event) => {
    event.preventDefault();

    // Tạo object chứa thông tin đăng nhập
    const loginData = {
      email: email,
      password: password,
    };

    // Gửi yêu cầu POST đến MockAPI để kiểm tra đăng nhập
    axios
      .post("https://63a572122a73744b008e28d5.mockapi.io/api/account/login", loginData)
      .then((response) => {
        console.log("Đăng nhập thành công:", response.data);
        // Thực hiện các xử lý sau khi đăng nhập thành công
      })
      .catch((error) => {
        console.error("Đăng nhập thất bại:", error);
        // Thực hiện các xử lý khi đăng nhập thất bại
      });
  };

  return (
    <div className="login-container">
      <h2>Login</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Email:</label>
          <input type="email" value={email} onChange={handleEmailChange} />
        </div>
        <div>
          <label>Password:</label>
          <input type="password" value={password} onChange={handlePasswordChange} />
        </div>
        <button type="submit">Login</button>
      </form>
    </div>
  );
};

export default Login;
