import React from 'react';

class Show extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      data: [],
    };
  }

  async componentDidMount() {
    const response = await fetch('https://63a5721a318b23efa793a770.mockapi.io/api/produce');
    const json = await response.json();
    this.setState({ data: json });
  }

  render() {
    return (
      <div className="container">
        <h3>Sản phẩm nổi bật</h3>
        <div className="card-list">
          {this.state.data.map((e) => (
            <div className="card" key={e.id}>
              <img src={e.avatar} alt={e.name} className="card-image" />
              <div className="card-content">
                <h4 className="card-title">{e.name}</h4>
                <p>{e.description}</p>
                <p>Quantity: {e.quantity}</p>
                <button className="btn btn-primary">Mua ngay</button>
                <a href="Create.js">
                  <button className="btn btn-primary">Thêm giỏ hàng</button>
                </a>
              </div>
            </div>
          ))}
        </div>
      </div>
    );
  }
}

export default Show;
