import React from 'react';

const Cart = ({ cartItems, removeFromCart, updateQuantity }) => {
  const handleIncreaseQuantity = (itemId) => {
    updateQuantity(itemId, 2); // Tăng số lượng lên 2
  };
  const placeOrder = () => {
		alert('Đặt hàng thành công!');
		clearCart();
	};
	const clearCart = async () => {
		try {
			await axios.delete("https://6410c403da042ca131fb737e.mockapi.io/gioHang");
			setCartItems([]);
			console.log("Xóa toàn bộ giỏ hàng thành công");
		} catch (error) {
			console.log("Lỗi khi xóa giỏ hàng:", error);
		}
	};
  return (
    <div className="cart">
      <h3>Giỏ hàng</h3>
      {cartItems.length > 0 ? (
        <table className="table table-bordered">
          <thead>
            <tr>
              <th>STT</th>
              <th>Tiêu đề</th>
              <th>Hình ảnh</th>
              <th>Giá</th>
              <th>Thao tác</th>
            </tr>
          </thead>
          <tbody>
            {cartItems.map((item, index) => (
              <tr key={item.id}>
                <td>{index + 1}</td>
                <td>{item.name}</td>
                <td>
                  <img src={item.image} alt={item.name} />
                </td>
                <td>{item.price}</td>
                <td>
                  <button className="btn btn-warning" onClick={() => removeFromCart(item.id)}>
                    Xóa
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      ) : (
        <p>Giỏ hàng trống</p>
      )}
    </div>
  );
};

export default Cart;
