import axios from "axios";
import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";

export const Cart = () => {
  const { id } = useParams();
  const [product, setProduct] = useState([]);
  const [cartItems, setCartItems] = useState([]);

  useEffect(() => {
    getproduct();
    loadCartItems();
  }, []);

  const removeItemFromCart = async (item) => {
    try {
      await axios.delete(`https://6410c403da042ca131fb737e.mockapi.io/gioHang/${item.id}`);
      const updatedItems = cartItems.filter((cartItem) => cartItem.id !== item.id);
      setCartItems(updatedItems);
      console.log("Xóa khỏi giỏ hàng thành công");
    } catch (error) {
      console.log("Lỗi khi xóa khỏi giỏ hàng:", error);
    }
  };

  const getproduct = async () => {
    try {
      const response = await axios.get(`https://6410c403da042ca131fb737e.mockapi.io/haisan/${id}`);
      setProduct(response.data);
    } catch (error) {
      console.log("Lỗi khi lấy sản phẩm:", error);
    }
  };

  const placeOrder = async () => {
    try {
      // Xóa toàn bộ giỏ hàng trong mock API
      await axios.delete("https://6410c403da042ca131fb737e.mockapi.io/gioHang");
      setCartItems([]);
      console.log("Xóa toàn bộ giỏ hàng thành công");
      alert("Đặt hàng thành công!");
    } catch (error) {
      console.log("Lỗi khi xóa giỏ hàng:", error);
    }
  };

  const loadCartItems = async () => {
    try {
      const response = await axios.get("https://6410c403da042ca131fb737e.mockapi.io/gioHang");
      setCartItems(response.data);
    } catch (error) {
      console.log("Lỗi khi tải giỏ hàng:", error);
    }
  };

  return (
    <div className="container">
      <h2>Giỏ hàng</h2>
      {cartItems.length > 0 ? (
        <table className="table table-bordered">
          <thead>
            <tr>
              <th>Tên sản phẩm</th>
              <th>Ảnh sản phẩm</th>
              <th>Giá</th>
              <th>Xóa</th>
            </tr>
          </thead>
          <tbody>
            {cartItems.map((item) => (
              <tr key={item.id}>
                <td>{item.name}</td>
                <td>
                  <img style={{ width: "100px", height: "100px" }} src={item.image} alt={item.name} />
                </td>
                <td>{item.price}</td>
                <td>
                  <button className="btn btn-warning" onClick={() => removeItemFromCart(item)}>Xóa</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      ) : (
        <p>Giỏ hàng trống</p>
      )}

      {cartItems.length > 0 && (
        <button className="btn btn-success" onClick={placeOrder}>Đặt hàng</button>
      )}
    </div>
  );
};

export default Cart;
