import axios from "axios"
import { useEffect, useState } from "react"
import { useParams } from "react-router-dom"
import ProductDetail from "./ProductDetail"

export const Shopping=()=>{ 
    const {id}=useParams()
    // const [product,setproduct]=useState([])

    // const [storeProduct,setStoreProduct]=useState({
    //     name:product.name,
    //     avatar:product.avatar,
    //     quantity:product.quantity
    // })
    const removeFromCart = (itemId) => {
        const updatedCart = Shopping.filter((item) => item.id !== itemId);
        Shopping(updatedCart);
      };
    

    useEffect(()=>{
        getproduct()
    },[])

    // useEffect(()=>{
    //      addshopping()
    // },[])
    // const addshopping=async()=>{
    // //     await axios.post("https://63a5721a318b23efa793a770.mockapi.io/api/Type_Product",{
    // //         name:product.name,
    // //         avatar:product.avatar,
    // //         quantity:product.quantity

    // //     })
    // // }

    const getproduct=async()=>{
        const value=await axios.get(`https://63a5721a318b23efa793a770.mockapi.io/api/produce/${id}`)
        await axios.post("https://63a5721a318b23efa793a770.mockapi.io/api/Type_Product",value.data)
    }
    return (
        <div className="cart">
      <h3>Giỏ hàng</h3>
        <table className="table table-bordered">
          <thead>
            <tr>
              <th>STT</th>
              <th>Tên sản phẩm</th>
              <th>Hình ảnh</th>
              <th>Giá</th>
              <th>Thao tác</th>
            </tr>
          </thead>
          <tbody>
            {Shopping.map((item, index) => (
              <tr key={item.id}>
                <td>{index + 1}</td>
                <td>{item.name}</td>
                <td>
                  <img src={item.avatar} alt={item.name} />
                </td>
                <td>{item.quatity}</td>
                <td>
                  <button className="btn btn-warning" onClick={() => removeFromCart(item.id)}>
                    Xóa
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      ) : (
        <p>Giỏ hàng trống</p>
      )}
    </div>
    )
}
