import axios from "axios";
import { useEffect, useState } from "react";
import { useParams, Link } from "react-router-dom";

export const Shopping = () => {
  const { id } = useParams();
  const [product, setProduct] = useState([]);

  useEffect(() => {
    getproduct();
  }, []);

  useEffect(() => {
    addshopping();
  }, []);

  const addshopping = async () => {
    try {
      await axios.post("https://63a5721a318b23efa793a770.mockapi.io/api/Type_Product", {
        name: product.name,
        avatar: product.avatar,
        quantity: product.quantity,
      });
      console.log("Thêm vào giỏ hàng thành công");
    } catch (error) {
      console.log("Lỗi khi thêm vào giỏ hàng:", error);
    }
  };

  const getproduct = async () => {
    try {
      const response = await axios.get(`https://63a5721a318b23efa793a770.mockapi.io/api/produce/${id}`);
      setProduct(response.data);
    } catch (error) {
      console.log("Lỗi khi lấy sản phẩm:", error);
    }
  };

  return (
    <table className="table table-bordered">
      <thead>
        <tr>
          <th>ID</th>
          <th>Tên sản phẩm</th>
          <th>Ảnh sản phẩm</th>
          <th>Gía</th>
          <th>Sửa</th>
          <th>Xóa</th>
        </tr>
      </thead>
      <tbody>
        {product.map((product) => (
          <tr key={product.id}>
            <td>{products.id}</td>
            <td>{products.name}</td>
            <td>
              <img className="img2" src={products.avatar} alt={products.name} />
            </td>
            <td>{products.quantity}</td>
            <td>
              <button className="btn btn-primary mr-2">
                <Link className="button" to={`/product/${products.id}`}>Chi tiết</Link>
              </button>
            </td>
            <td></td>
          </tr>
        ))}
      </tbody>
    </table>
  );
};

export default Shopping;
