import axios from "axios";
import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";

export const Shopping = () => {
  const { id } = useParams();
  const [product, setProduct] = useState({});
  const [cartItems, setCartItems] = useState([]);

  useEffect(() => {
    getproduct();
    loadCartItems();
  }, []);

  const addshopping = async () => {
    try {
      await axios.post("https://63a5721a318b23efa793a770.mockapi.io/api/Type_Product", {
        name: product.name,
        avatar: product.avatar,
        quantity: product.quantity
      });
      setCartItems([...cartItems, product]);
      console.log("Thêm vào giỏ hàng thành công");
    } catch (error) {
      console.log("Lỗi khi thêm vào giỏ hàng:", error);
    }
  };

  const removeFromCart = async (itemId) => {
    const updatedCart = cartItems.filter((item) => item.id !== itemId);
    setCartItems(updatedCart);
    try {
      await axios.delete(`https://63a5721a318b23efa793a770.mockapi.io/api/Type_Product/${itemId}`);
      console.log("Xóa hàng thành công");
    } catch (error) {
      console.log("Lỗi khi xóa:", error);
    }
  };

  const getproduct = async () => {
    try {
      const response = await axios.get(`https://63a5721a318b23efa793a770.mockapi.io/api/produce/${id}`);
      setProduct(response.data);
    } catch (error) {
      console.log("Lỗi khi lấy sản phẩm:", error);
    }
  };

  const loadCartItems = () => {
    const storedCartItems = JSON.parse(localStorage.getItem("cartItems")) || [];
    setCartItems(storedCartItems);
  };

  const saveCartItems = () => {
    localStorage.setItem("cartItems", JSON.stringify(cartItems));
  };

  const handleBack = () => {
    saveCartItems();
    // Add any additional logic or redirection as needed
  };

  const handleClearCart = () => {
    setCartItems([]);
    localStorage.removeItem("cartItems");
    console.log("Đã xóa giỏ hàng");
  };

  return (
    <div className="container">

      <div className="cart">
        <h3>Giỏ hàng</h3>
        {cartItems.length > 0 ? (
          <table className="table table-bordered">
            <thead>
              <tr>
                <th>STT</th>
                <th>Tên</th>
                <th>Hình ảnh</th>
                <th>Số lượng</th>
                <th>Thao tác</th>
              </tr>
            </thead>
            <tbody>
              {cartItems.map((item, index) => (
                <tr key={item.id}>
                                  <td>{index + 1}</td>
                  <td>{item.name}</td>
                  <td>
                    <img src={item.avatar} alt={item.name} />
                  </td>
                  <td>{item.quantity}</td>
                  <td style={{ display: "flex", gap: "10px" }}>
                    <button
                      className="btn btn-warning"
                      onClick={() => removeFromCart(item.id)}
                    >
                      Xóa
                    </button>
                    <button className="btn btn-warning">Mua ngay</button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        ) : (
          <p>Giỏ hàng trống</p>
        )}
        
        {cartItems.length > 0 && (
          <button className="btn btn-danger" onClick={handleClearCart}>
            Xóa giỏ hàng
          </button>
        )}
      </div>
    </div>
  );
};
