import React from 'react';
import { Link, useHistory } from 'react-router-dom';

const Cart = ({ cartItems, removeFromCart }) => {
  const history = useHistory();

  const handleRemoveFromCart = (itemId) => {
    removeFromCart(itemId);
    history.push('/cart');
  };

  return (
    <div className="cart">
      <h3>Giỏ hàng</h3>
      {cartItems.length > 0 ? (
        <table className="table table-bordered">
          <tbody>
            {cartItems.map((item, index) => (
              <tr key={item.id}>
                <td>{index + 1}</td>
                <td>{item.name}</td>
                <td>
                  <img src={item.avatar} alt={item.name} />
                </td>
                <td>{item.quatity}</td>
                <td>
                  <Link to={`/cart/${item.id}`} onClick={() => handleRemoveFromCart(item.id)} className="btn btn-danger">
                    Xóa
                  </Link>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      ) : (
        <p>Giỏ hàng trống</p>
      )}
    </div>
  );
};

export default Cart;
