import React, { Component } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

class Edit extends Component {
  constructor(props) {
    super(props);
    this.state = {
      product: {
        name: '',
        avatar: '',
        quatity: '',
        description: '',
      }
    };
  }

  handleInputChange = (event) => {
    const { name, value } = event.target;
    this.setState(prevState => ({
      product: {
        ...prevState.product,
        [name]: value
      }
    }));
  };

  handleSubmit = async (event) => {
    event.preventDefault();
    const { id } = this.props.match.params;
    const { product } = this.state;

    try {
      await axios.put(`https://63a5721a318b23efa793a770.mockapi.io/api/produce/${id}`, product);
      this.setState({
        product: {
          name: '',
          quatity: '',
          avatar: '',
          description: '',
          id: 0,
        }
      });

      alert('Product edited successfully!');

      setTimeout(() => {
        window.location ='/Admin';
      }, 100);
    } catch (error) {
      console.log('Error adding product:', error);
    }
  };

  componentDidMount() {
    const { id } = this.props.match.params;
    fetch(`https://63a5721a318b23efa793a770.mockapi.io/api/produce/${id}`)
      .then(response => response.json())
      .then(data => this.setState({ product: data }));
  }

  render() {
    const { product } = this.state;

    return (
      <div className="container">
      <div className="row">
        <div className="col-sm-12">
          <div className="card">
            <div className="card-body">
            <div className="form-group">
                <label>ID</label>
                <input type="text" className="form-control" value={product.name} onChange={this.handleInputChange} name="name" />
              </div>
              <div className="form-group">
                <label>Tên Sản phẩm</label>
                <input type="text" className="form-control" value={newProduct.name} onChange={this.handleInputChange} name="name" />
              </div>
              <div className="form-group">
                <label>Ảnh sản phẩm</label>
                <input type="text" className="form-control" value={newProduct.avatar} onChange={this.handleInputChange} name="avatar" />
              </div>
              <div className="form-group">
                <label>Gía</label>
                <input type="text" className="form-control" value={newProduct.quatity} onChange={this.handleInputChange} name="quatity" /> {/* Sửa thành 'quantity' */}
              </div>
              <div className="form-group">
                <label>Mô tả</label>
                <input type="text" className="form-control" value={newProduct.description} onChange={this.handleInputChange} name="description" /> {/* Sửa thành 'quantity' */}
              </div>
              <button type="submit" className="btn btn-primary" onClick={this.handleSubmit}>Add</button>
            </div>
          </div>
        </div>
      </div>
    </div>
    );
  }
}

export default Edit;
